select count(1)
from
(select c2.APP_ID,c2.OPERATER,c2.OPERATE_DESC,
c2.OPERATE_RESULT,c2.CRT_DATE
from
OPAS_BIZ_INP_APPL_C1 c1 ,
OPAS_APPLY_LIFE_CICLE c2
where
c1.APP_ID = c2.APP_ID

	and c2.APP_ID = #{appId}


	and c2.OPERATER = #{operater}


	and c2.OPERATE_DESC = #{operateDesc}


	and c2.OPERATE_RESULT = #{operateResult}


	and c2.CRT_DATE = #{crtDate}

order by c2.CRT_DATE asc)

select
c2.APP_ID,c2.OPERATER,c2.OPERATE_DESC,
c2.OPERATE_RESULT,c2.CRT_DATE
