c1.APP_ID = c2.APP_ID

	and c2.APP_ID = #{appId}


	and c2.REMARK_INFO = #{remarkInfo}


	and c2.REMARK_USER = #{remarkUser}


	and c2.REMARK_TIME = #{remarkTime}


	and c2.CURR_NODE = #{currNode}

)

