	/**
	 * @Title: elseif
	 *@Description: TODO if...else...if语句
	 *@param num
	 *@author: LiuWei
	 *@Date: 2020年8月18日上午10:19:51
	 */
	private static void elseif(Integer num){
		if(num >10){
			System.out.println("num > 10");
		}else if(num > 7){
			System.out.println("num > 7");
		}else if(num > 4){
			System.out.println("num > 4");
		}else{
			System.out.println("num < 4");
		}
	}
